var assert = require('chai').assert;

var LightLevel = require('./index');

suite('Basic Usage', function() {

    describe('LightLevel', function() {
        it('should be able to access the LightLevel object', function(done) {
            assert.isObject(LightLevel);
            done();
        });
    });

    describe('LightLevel.returnStream()', function() {
        it('should create and return a Stream with the values of the OSX light sensor', function(done) {
            var stream = LightLevel.returnStream();
            assert.isObject(stream);
            assert.isTrue(stream.writable);
            assert.isTrue(stream.readable);
            done();
        });
    });

    describe('LightLevel.stopStream()', function() {
        it('should create and return a Stream with the values of the OSX light sensor', function(done) {
            LightLevel.stopStream();
            done();
        });
    });
});

